import { useContext, useState } from 'react'

import Modal from '../UI/Modal'
import classes from './Cart.module.css'
import CartContext from '../../store/cart-contex'
import CartItem from './CartItem'
import Checkout from './Checkout'

const Cart = props =>{
    const [isCheckOut, setIsCheckOut] = useState(false);
    const cartCtx = useContext(CartContext)
    const totalAmount = `$ ${cartCtx.totalAmount.toFixed(2)}`
    const hasItems = cartCtx.items.length > 0
    const [isSubmitting, setIsSubmitting] = useState()

    const cartItemRemoveHandler = id =>{
        cartCtx.removeItem(id)
    }

    const cartItemAddHandler = item =>{
        cartCtx.addItem({...item, amount:1})
    }

    const orderHandler=()=>{
        setIsCheckOut(true)
    }

    const submitOrderHandler = async (userData)=>{
        setIsSubmitting(true)
        await fetch('https://meal-orderlist-reactjs-default-rtdb.europe-west1.firebasedatabase.app/orders.json', {
            method:'POST',
            body:JSON.stringify({
                user:userData,
                orderItems: cartCtx.items
            })
        })
        setIsSubmitting(false)
    }

    const cartItems = <ul className={classes['cart-items']}>
        {cartCtx.items.map((item)=><CartItem key={item.id} name={item.name} amount={item.amount} price={item.price}
        onRemove={cartItemRemoveHandler.bind(null, item.id)} onAdd={cartItemAddHandler.bind(null, item)}/>)}
        </ul>

    const modalActions = (<div className={classes.actions}>
        <button className={classes['button--alt']} onClick={props.onClose}>Close</button>
        {hasItems && <button className={classes.button} onClick={orderHandler}>Order</button>}
    </div>)

    return <Modal onClose={props.onClose}>
        {cartItems}
        <div className={classes.total}>
            <span>Total Amount</span>
            <span>{totalAmount}</span>
        </div>
        {isCheckOut && <Checkout onConfirm={submitOrderHandler} onCancel={props.onClose}/>}
        {!isCheckOut && modalActions}
    </Modal>
}

export default Cart
