import classes from './AvailableMeals.module.css'
import MealItem from './MealItem/MealItem';
import Card from '../UI/Card';
import { useEffect, useState } from 'react';


// const DUMMY_MEALS = [
//     {
//       id: 'm1',
//       name: 'Sushi',
//       description: 'Finest fish and veggies',
//       price: 22.99,
//     },
//     {
//       id: 'm2',
//       name: 'Schnitzel',
//       description: 'A german specialty!',
//       price: 16.5,
//     },
//     {
//       id: 'm3',
//       name: 'Barbecue Burger',
//       description: 'American, raw, meaty',
//       price: 12.99,
//     },
//     {
//       id: 'm4',
//       name: 'Green Bowl',
//       description: 'Healthy...and green...',
//       price: 18.99,
//     },
//   ];

const AvailableMeals = ()=>{
    const [meals, setMeals] = useState([]);
    useEffect(()=>{
      const fetchMeals = async ()=>{
        const response = await fetch('https://meal-orderlist-reactjs-default-rtdb.europe-west1.firebasedatabase.app/meals.json');
        const responseData = await response.json()

        const loadedMeals = []

        for(const key of responseData){
          loadedMeals.push({
            id:key,
            name: responseData[key].name,
            description:responseData[key].description,
            price:responseData[key].price,
          })
        }
        setMeals(loadedMeals)
      }
      fetchMeals()
    }, [])

    const mealsList = meals.map((meal) => <MealItem key={meal.id} meal={meal}>{meal.name}</MealItem>)

    return <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
}

export default AvailableMeals;
